// Database types for Byte Bridges Enterprise
// Generated: 2025-11-08

export interface BlogPost {
  id: number;
  slug: string;
  title: string;
  excerpt: string | null;
  content: string;
  featured_image: string | null;
  author_id: string | null; // UUID
  category: string | null;
  tags: string[] | null;
  published: boolean;
  published_at: Date | null;
  created_at: Date;
  updated_at: Date;
}

export interface CaseStudy {
  id: number;
  slug: string;
  title: string;
  client_name: string;
  industry: string;
  services_used: string[] | null;
  challenge: string;
  solution: string;
  results: string;
  technologies: string[] | null;
  featured_image: string | null;
  metrics: Record<string, any> | null; // JSONB
  published: boolean;
  published_at: Date | null;
  created_at: Date;
  updated_at: Date;
}

export interface Testimonial {
  id: number;
  client_name: string;
  client_company: string;
  client_role: string | null;
  client_photo: string | null;
  quote: string;
  rating: number | null; // 1-5
  industry: string | null;
  service_related: string | null;
  featured: boolean;
  display_order: number;
  published: boolean;
  created_at: Date;
  updated_at: Date;
}

export interface ContactSubmission {
  id: number;
  name: string;
  email: string;
  phone: string | null;
  company: string | null;
  service_interest: string[] | null;
  budget_range: string | null;
  timeline: string | null;
  message: string;
  source: string | null;
  utm_source: string | null;
  utm_medium: string | null;
  utm_campaign: string | null;
  status: 'new' | 'contacted' | 'in_progress' | 'converted' | 'closed';
  admin_notes: string | null;
  contacted_at: Date | null;
  gdpr_consent: boolean;
  marketing_consent: boolean;
  retention_expiry: Date | null;
  created_at: Date;
  updated_at: Date;
}

// Insert types (omit auto-generated fields)
