export type CaseStudyUpdate = Partial<Omit<CaseStudy, 'id' | 'created_at'>> & { id: number };
export type TestimonialUpdate = Partial<Omit<Testimonial, 'id' | 'created_at'>> & { id: number };
export type ContactSubmissionUpdate = Partial<Omit<ContactSubmission, 'id' | 'created_at'>> & { id: number };

