// GET /api/blog - List blog posts
export async function GET(request: NextRequest) {
  try {
    const searchParams = request.nextUrl.searchParams;

    // Parse pagination and filters
    const page = parseInt(searchParams.get('page') || '1');
    const limit = parseInt(searchParams.get('limit') || '20');
    const category = searchParams.get('category');
    const published = searchParams.get('published') === 'true' ? true : searchParams.get('published') === 'false' ? false : undefined;
    const search = searchParams.get('search');

    const offset = (page - 1) * limit;

    // Build query
    let query = `
      SELECT
        id, slug, title, excerpt, content, featured_image,
        author_id, category, tags, published, published_at,
        created_at, updated_at
      FROM blog_posts
      WHERE 1=1
    `;

    const params: any[] = [];
    let paramIndex = 1;

    if (category) {
      query += ` AND category = $${paramIndex}`;
      params.push(category);
      paramIndex++;
    }

    if (published !== undefined) {
      query += ` AND published = $${paramIndex}`;
      params.push(published);
      paramIndex++;
    }

    if (search) {
      query += ` AND search_vector @@ plainto_tsquery('english', $${paramIndex})`;
      params.push(search);
      paramIndex++;
    }

    query += ` ORDER BY published_at DESC NULLS LAST, created_at DESC`;
    query += ` LIMIT $${paramIndex} OFFSET $${paramIndex + 1}`;
    params.push(limit, offset);

    // Get total count
    let countQuery = 'SELECT COUNT(*) FROM blog_posts WHERE 1=1';
    const countParams: any[] = [];
    let countParamIndex = 1;

    if (category) {
      countQuery += ` AND category = $${countParamIndex}`;
      countParams.push(category);
      countParamIndex++;
    }

    if (published !== undefined) {
      countQuery += ` AND published = $${countParamIndex}`;
      countParams.push(published);
      countParamIndex++;
    }

    if (search) {
      countQuery += ` AND search_vector @@ plainto_tsquery('english', $${countParamIndex})`;
      countParams.push(search);
    }

    const [result, countResult] = await Promise.all([
      db.query(query, params),
      db.query(countQuery, countParams),
    ]);

    const total = parseInt(countResult.rows[0].count);

    return NextResponse.json({
      data: result.rows,
      pagination: {
        page,
        limit,
        total,
        pages: Math.ceil(total / limit),
      },
    });
  } catch (error) {
