      updates.push(`rating = $${paramIndex}`);
      values.push(validated.rating);
      paramIndex++;
    }
    if (validated.industry !== undefined) {
      updates.push(`industry = $${paramIndex}`);
      values.push(validated.industry);
      paramIndex++;
    }
    if (validated.service_related !== undefined) {
      updates.push(`service_related = $${paramIndex}`);
      values.push(validated.service_related);
      paramIndex++;
    }
    if (validated.featured !== undefined) {
      updates.push(`featured = $${paramIndex}`);
      values.push(validated.featured);
      paramIndex++;
    }
    if (validated.display_order !== undefined) {
      updates.push(`display_order = $${paramIndex}`);
      values.push(validated.display_order);
      paramIndex++;
    }
    if (validated.published !== undefined) {
      updates.push(`published = $${paramIndex}`);
      values.push(validated.published);
      paramIndex++;
    }

    if (updates.length === 0) {
      return NextResponse.json(
        { error: 'No fields to update' },
        { status: 400 }
      );
    }

    values.push(parseInt(id));

    const query = `
      UPDATE testimonials
      SET ${updates.join(', ')}
      WHERE id = $${paramIndex}
      RETURNING *
    `;

    const result = await db.query(query, values);

    if (result.rows.length === 0) {
      return NextResponse.json(
        { error: 'Testimonial not found' },
        { status: 404 }
      );
    }

    return NextResponse.json(result.rows[0]);
  } catch (error: any) {
    console.error('Error updating testimonial:', error);

    if (error.name === 'ZodError') {
      return NextResponse.json(
        { error: 'Validation failed', details: error.errors },
        { status: 400 }
      );
    }

    return NextResponse.json(
      { error: 'Failed to update testimonial' },
      { status: 500 }
    );
  }
}

// DELETE /api/testimonials/[id] - Delete testimonial (admin only)
export async function DELETE(
  request: NextRequest,
  { params }: RouteParams
) {
  try {
    // Check authentication
    const session = await getServerSession(authOptions);
    if (!session?.user) {
      return NextResponse.json(
        { error: 'Unauthorized' },
        { status: 401 }
      );
    }

    const { id } = params;

    const result = await db.query(
      'DELETE FROM testimonials WHERE id = $1 RETURNING id',
      [parseInt(id)]
    );

    if (result.rows.length === 0) {
      return NextResponse.json(
        { error: 'Testimonial not found' },
        { status: 404 }
      );
    }

    return NextResponse.json({ success: true, id: result.rows[0].id });
  } catch (error) {
    console.error('Error deleting testimonial:', error);
    return NextResponse.json(
      { error: 'Failed to delete testimonial' },
      { status: 500 }
    );
  }
}

