'use client';

import { useState, useEffect } from 'react';
import { useSession } from 'next-auth/react';
import { useRouter } from 'next/navigation';
import { motion } from 'framer-motion';
import {
  LayoutDashboard,
  Users,
  FolderOpen,
  FileText,
  CreditCard,
  Settings,
  HelpCircle,
  LogOut,
  Bell,
  TrendingUp,
  Shield,
  Activity,
  DollarSign,
  UserCheck,
  AlertTriangle,
  Menu,
  X,
  Search,
  Filter
} from 'lucide-react';
import { signOut } from 'next-auth/react';

interface AdminStats {
  totalUsers: number;
  activeProjects: number;
  totalRevenue: number;
  openTickets: number;
  newUsers: number;
  systemHealth: number;
}

export default function AdminPage() {
  const { data: session, status } = useSession();
  const router = useRouter();
  const [activeSection, setActiveSection] = useState('overview');
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [stats, setStats] = useState<AdminStats>({
    totalUsers: 247,
    activeProjects: 42,
    totalRevenue: 125480,
    openTickets: 8,
    newUsers: 23,
    systemHealth: 98
  });

  useEffect(() => {
    if (status === 'unauthenticated') {
      router.push('/signin');
    } else if (session?.user?.role !== 'admin') {
      router.push('/dashboard');
    }
  }, [status, session, router]);

  if (status === 'loading') {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  if (!session || session.user?.role !== 'admin') {
    return null;
  }

  const sidebarItems = [
    { id: 'overview', label: 'Overview', icon: LayoutDashboard },
    { id: 'users', label: 'Users', icon: Users },
    { id: 'projects', label: 'Projects', icon: FolderOpen },
    { id: 'invoices', label: 'Invoices', icon: CreditCard },
    { id: 'tickets', label: 'Support Tickets', icon: HelpCircle },
    { id: 'security', label: 'Security', icon: Shield },
    { id: 'system', label: 'System', icon: Activity },
    { id: 'settings', label: 'Settings', icon: Settings },
  ];

  const renderContent = () => {
    switch (activeSection) {
      case 'overview':
        return <AdminOverview stats={stats} />;
      case 'users':
        return <UsersManagement />;
      case 'projects':
        return <ProjectsManagement />;
      case 'invoices':
        return <InvoicesManagement />;
      case 'tickets':
        return <TicketsManagement />;
      case 'security':
        return <SecuritySection />;
      case 'system':
        return <SystemSection />;
      case 'settings':
        return <AdminSettings />;
      default:
        return <AdminOverview stats={stats} />;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-premium surface-dark">
      {/* Mobile Sidebar Toggle */}
      <button
        onClick={() => setIsSidebarOpen(!isSidebarOpen)}
        className="lg:hidden fixed top-4 left-4 z-50 p-2 glass-morphism-dark rounded-lg"
      >
        {isSidebarOpen ? <X className="w-6 h-6 text-white" /> : <Menu className="w-6 h-6 text-white" />}
      </button>

      {/* Sidebar */}
      <aside className={`fixed inset-y-0 left-0 z-40 w-64 bg-slate-900 shadow-xl transform transition-transform lg:translate-x-0 ${
        isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
      }`}>
        <div className="h-full flex flex-col">
          <div className="p-6 border-b border-slate-700">
            <h2 className="text-2xl font-bold text-white">Admin Panel</h2>
            <p className="text-sm text-slate-400 mt-1">Byte Bridges Control</p>
          </div>

          <nav className="flex-1 p-4">
            {sidebarItems.map((item) => (
              <button
                key={item.id}
                onClick={() => {
                  setActiveSection(item.id);
                  setIsSidebarOpen(false);
                }}
                className={`w-full flex items-center gap-3 px-4 py-3 rounded-lg transition-colors mb-2 ${
                  activeSection === item.id
                    ? 'bg-blue-600 text-white'
                    : 'text-slate-300 hover:bg-slate-800 hover:text-white'
                }`}
              >
                <item.icon className="w-5 h-5" />
                <span className="font-medium">{item.label}</span>
              </button>
            ))}
          </nav>

          <div className="p-4 border-t border-slate-700">
            <div className="flex items-center gap-3 mb-4">
              <div className="w-10 h-10 bg-gradient-to-r from-blue-600 to-purple-600 rounded-full flex items-center justify-center">
                <Shield className="w-5 h-5 text-white" />

