              <button className="p-2 text-slate-400 hover:text-white">
                <Settings className="w-6 h-6" />
              </button>
            </div>
          </div>
        </header>

        <div className="p-6">
          {renderContent()}
        </div>
      </main>
    </div>
  );
}

